import Card from "../UI/Card";
import classes from "./AddUser.module.css";
import Button from "../UI/Button";
import { useState, useRef } from "react";
import ErrorModal from "../UI/ErrorModal";

const AddUser = (props) => {
  const [error, setError] = useState();
  const enteredNameRef = useRef();
  const enteredAgeRef = useRef();

  const addUserHandler = (event) => {
    event.preventDefault();
    const enteredName = enteredNameRef.current.value;
    const enteredAge = enteredAgeRef.current.value;
    if (enteredName.trim().length === 0 || enteredAge.trim().length === 0) {
      setError({
        title: "Invalid empty name/age input",
        message: "Please set a valid name and age (non-empty values)",
      });
      return;
    }
    if (+enteredAge < 1) {
      setError({
        title: "Invalid age input",
        message: "Please set a valid age (Age > 0)",
      });
      return;
    }
    props.onAddUser(enteredName, enteredAge);
  };
  const errorHandler = () => {
      setError(null);
  }
  return (
    <>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={errorHandler}
        />
      )}
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="username">Username</label>
          <input
            id="username"
            type="text"
            ref={enteredNameRef}
          />
          <label htmlFor="age">Age (In Years)</label>
          <input
            id="age"
            type="number"
            ref={enteredAgeRef}
          />
          <Button type="submit" >Add User</Button>
        </form>
      </Card>
    </>
  );
};

export default AddUser;
